

$(document).ready(function(){
 
    function newGrid(sqrs){

    	if ( isNaN( sqrs ) ) {
			sqrs = 16;
		}

		var width = 512/sqrs;
		$('.squares').detach();
    	for(var i = 1; i <= (sqrs*sqrs); i++){	
			$('.container').append("<div class=\"squares\" ></div>");
	    }


		var $sqrs = $('.squares');
		$sqrs.width(width);
		$sqrs.height(width);
		$sqrs.css("background", "white");
		$sqrs.css("float", "right");
		$sqrs.css("border-style", "solid");
		$sqrs.css("border-width", "0");

    	
		
		$('#rand').click(function(){
			$sqrs.on("mouseenter", function(){
				$(this).css("background", '#'+Math.floor(Math.random()*16777215).toString(16));
			});
		});

		$('#grey').click(function(){
			$sqrs.on("mouseenter", function(){
				$(this).css("background", "black");
			});
		});

		$('#grad').click(function(){
			$sqrs.css("background-color", "black");
			$sqrs.css("opacity", "0");
			$sqrs.on("mouseenter", function(){
				$(this).css("opacity", "+=0.1");
			});
		});

		$sqrs.on("mouseenter", function(){
			$(this).css("background", "black");
		});


    }

    newGrid(16);



	$('#new').click(function(){
		var numSqrs = parseFloat(prompt("How many squares do you want? (1-64)"));
		newGrid(numSqrs);
	})
    

	$('#clr').click(function(){
		$('.squares').css("background", "white");
	})
	

	
	

})